

function drawLine() {
    var data = google.visualization.arrayToDataTable([["Year", "Sales"], ["2010", 1880], ["2011", 530], ["2012", 425], ["2013", 750], ["2014", 550], ["2015", 880], ["2016", 880], ["2017", 880], ["2018", 880], ["2019", 880], ["2020", 880]]),
        options_line = {
            height: 400,
            fontSize: 12,
            //curveType: "function",
            colors: ["#37BC9B",
                "#DA4453"],
            pointSize: 5,
            chartArea: {
                left: "5%", width: "90%", height: 350
            }
            ,
            vAxis: {
                gridlines: {
                    color: "#e9e9e9", count: 10
                }
                ,
                minValue: 0,
                title: 'Sales in $',
                titleTextStyle: { italic: false }
            }
            ,
            legend: {
                position: "top",
                alignment: "center",
                textStyle: {
                    fontSize: 12
                }
            }

        }
        ,
        line = new google.visualization.LineChart(document.getElementById("line-chart1"));
    line.draw(data, options_line)
}

google.load("visualization", "1.0", {
    packages: ["corechart"]
}

),
    google.setOnLoadCallback(drawLine),
    $(function () {
        function resize() {
            drawLine()
        }
        $(window).on("resize", resize), $(".menu-toggle").on("click", resize)
    }

    );